import { containerConfiguration } from '../../containers';

import { AdyenComponent, BraintreeComponent, StripeComponent } from './paymentGateways';
import PaymentGatewayComponent from './paymentGatewayComponent';

export default function createFinanceContainerConfiguration() {
    return containerConfiguration()
        .register(AdyenComponent)
        .register(BraintreeComponent)
        .register(StripeComponent)
        .register(PaymentGatewayComponent, [AdyenComponent, BraintreeComponent, StripeComponent]);
}
