import { type } from '../../../utils';

export default function CultureInfo(data) {
    return type.create(CultureInfo, {
        getCode() {
            return data.code;
        },

        getCurrencyInfo() {
            return data.currency;
        },

        getDateTimeInfo() {
            return data.dateTime;
        },

        isRightToLeft() {
            return data.isRightToLeft;
        },

        getNumberInfo() {
            return data.number;
        },
    });
}
