import { containerConfiguration } from '../../containers';
import { CultureInfoComponent } from './cultureInfos';
import { CultureComponent } from './cultures';
import { CurrencyComponent } from './currencies';
import { CountryComponent } from './countries';
import { DataComponent } from './data';
import { DateComponent } from './dates';
import { DateTimeComponent } from './dateTimes';
import { DateTimeUtcComponent } from './dateTimesUtc';
import { LanguageComponent } from './languages';
import { LocalizationComponent } from './localizations';
import { MonthComponent } from './months';
import { NumberComponent } from './numbers';
import { TextComponent } from './texts';
import { TimeComponent } from './times';
import { YearComponent } from './years';
import { WallClockDateComponent } from './wallClockDates';
import { WallClockMonthComponent } from './wallClockMonths';
import { WallClockCalendarComponent } from './calendars';

export default function createGlobalizationContainerConfiguration(globalizationData) {
    return containerConfiguration()
        .register(DataComponent, [], globalizationData)
        .register(LanguageComponent, [DataComponent])
        .register(CultureComponent, [DataComponent])
        .register(CultureInfoComponent, [DataComponent])
        .register(CurrencyComponent, [DataComponent])
        .register(CountryComponent, [DataComponent])
        .register(LocalizationComponent, [LanguageComponent, CultureComponent])
        .register(TextComponent, [DataComponent])
        .register(NumberComponent, [CultureInfoComponent])
        .register(DateTimeUtcComponent)
        .register(DateTimeComponent, [DateTimeUtcComponent, CultureInfoComponent])
        .register(DateComponent, [CultureInfoComponent])
        .register(TimeComponent, [CultureInfoComponent])
        .register(MonthComponent, [CultureInfoComponent])
        .register(YearComponent, [CultureInfoComponent])
        .register(WallClockDateComponent, [CultureInfoComponent])
        .register(WallClockMonthComponent, [CultureInfoComponent])
        .register(WallClockCalendarComponent, [WallClockDateComponent, WallClockMonthComponent, CultureInfoComponent]);
}
