import { type } from '../../../utils';

export default function Language(data, fallbackLanguage) {
    return type.create(Language, {
        getCode() {
            return data.code;
        },

        getEnglishName() {
            return data.englishName;
        },

        getLocalName() {
            return data.localName;
        },

        isDefault() {
            return data.isDefault;
        },

        getFallbackLanguage() {
            return fallbackLanguage;
        },
    });
}
