import type from '../../../utils/type';
import Localization from '../localizations/localization';
import ensureCultureInfo from './ensureCultureInfo';
import isValidMoment from './isValidMoment';

export default function isValidFormat(value, localization, formatStringName, cultureInfoComponent) {
    type.check(localization, Localization);

    const cultureInfo = ensureCultureInfo(localization.getCulture(), cultureInfoComponent);
    return isValidMoment(value, cultureInfo.getDateTimeInfo()[formatStringName]);
}
