import moment from 'moment-timezone';

const STRICT = true;
export default function toMoment(value, formats, timezone = 'Etc/Utc') {
    const m = moment.tz(value, formats, STRICT, timezone);

    if (!m.isValid()) {
        throw new TypeError('The argument is not in a supported format: ' + value);
    }

    return m;
}
