import React, { Component } from 'react';
import { localizationPropTypes } from './propTypes';

class LocalizedProperty extends Component {
    static contextTypes = {
        localization: localizationPropTypes,
    }

    componentDidMount() {
        this.unsubscribe = this.context.localization.subscribe(() => this.forceUpdate());
    }

    componentWillUnmount() {
        this.unsubscribe();
    }

    render() {
        const { property, ...other } = this.props;
        const localizedProperty = this.context.localization.localizeProperty(property);

        return (
            <span {...other}>{localizedProperty}</span>
        );
    }
}

export default LocalizedProperty;
