import { containerConfiguration } from './containers';
import { financeContainerConfiguration } from './components/finance';
import { globalizationContainerConfiguration } from './components/globalization';

import {
    ConfigurationComponent,
    LoggerComponent,
    ErrorReporterComponent,
    FacebookComponent,
    RouterComponent,
    SentryComponent,
} from './components';

export default function createSharedContainerConfiguration(data) {
    return containerConfiguration()
        .extend(financeContainerConfiguration())
        .extend(globalizationContainerConfiguration(data.globalization))

        .register(ConfigurationComponent, [], data.configuration)
        .register(FacebookComponent, [ConfigurationComponent])
        .register(LoggerComponent, [ConfigurationComponent])
        .register(RouterComponent, [ConfigurationComponent], data.router)
        .register(SentryComponent, [ConfigurationComponent])

        .register(ErrorReporterComponent, [LoggerComponent, SentryComponent]);
}
