import _ from 'lodash';

export function getDateString(date) {
    return date.getFullYear() + '-' + padZeros(date.getMonth() + 1) + '-' + padZeros(date.getDate());
}

export function getDateTimeString(date) {
    return getDateString(date) + 'T' + padZeros(date.getHours()) + ':' + padZeros(date.getMinutes())
        + ':' + padZeros(date.getSeconds()) + '.' + padZeros(date.getMilliseconds(), 3);
}

function padZeros(value, zeros = 2) {
    return _.padStart(value, zeros, '0');
}
