import React, { Component } from 'react';
import { PropTypes as pt } from 'prop-types';
import { AvatarElement, IconElement, TitleElement } from './style';

export default class Avatar extends Component {
    static propTypes = {
        alt: pt.string,
        children: pt.node,
        icon: pt.string,
        title: pt.string,
    };

    static defaultProps = {
        alt: '',
    };

    render() {
        const { alt, children, icon, title } = this.props;

        return (
            <AvatarElement>
                {children}
                {icon && <IconElement value={icon} alt={alt} />}
                {title && <TitleElement>{title[0]}</TitleElement>}
            </AvatarElement>
        );
    }
}
