import React from 'react';
import { PropTypes as pt } from 'prop-types';
import styled from 'styled-components';
import { DEFAULT_THEME } from '../variables';

const FontIconElement = styled.span`
    color: ${props => props.theme.iconColor};
`;
FontIconElement.defaultProps = {
    theme: DEFAULT_THEME,
};

const FontIcon = ({ alt, value, className }) => (
    <FontIconElement aria-label={alt} className={className}>
        <span aria-hidden="true">{value}</span>
    </FontIconElement>
);

FontIcon.propTypes = {
    alt: pt.string,
    className: pt.string,
    value: pt.string,
};

FontIcon.defaultProps = {
    alt: '',
};

const MaterialFontIcon = styled(FontIcon)`
    font-family: 'Material Icons', 'Material_Icons';
    font-weight: normal;
    font-style: normal;
    font-size: 24px;  /* Preferred icon size */
    display: inline-block;
    line-height: 1;
    text-transform: none;
    letter-spacing: normal;
    word-wrap: normal;
    white-space: nowrap;
    direction: ltr;

    /* Support for all WebKit browsers. */
    -webkit-font-smoothing: antialiased;
    /* Support for Safari and Chrome. */
    text-rendering: optimizeLegibility;

    /* Support for Firefox. */
    -moz-osx-font-smoothing: grayscale;

    /* Support for IE. */
    font-feature-settings: 'liga';
`;

export default MaterialFontIcon;
