import React, { Component } from 'react';
import { PropTypes as pt } from 'prop-types';
import Radio from './Radio';
import { RadioButtonElement, RadioLabelElement } from './style';

export default class RadioButton extends Component {
    static propTypes = {
        label: pt.node,
        name: pt.string,
    };

    render() {
        const { label, ...other } = this.props;
        return (
            <RadioButtonElement>
                <Radio {...other} />
                {label && <RadioLabelElement htmlFor={this.props.name}>{label}</RadioLabelElement>}
            </RadioButtonElement>
        );
    }
}