import styled from 'styled-components';
import { FONT_WEIGHT_THIN, PREFERRED_FONT } from '../variables';
import { FIELD_TEXT_NORMAL_SIZE, FIELD_TEXT_LARGE_SIZE } from '../Field/style';

export const TextareaElement = styled.textarea`
    background: transparent;
    border: 0;
    color: ${props => props.theme.primaryTextColor};
    font-family: ${PREFERRED_FONT};
    font-size: ${props => (props.large ? FIELD_TEXT_LARGE_SIZE : FIELD_TEXT_NORMAL_SIZE)};
    font-weight: ${FONT_WEIGHT_THIN};
    min-height: ${props => (props.large ? '30px' : '20px')};
    outline: none;
    padding: 0;
    resize: none;
    width: 100%;
`;
