import React, { Component } from 'react';
import { PropTypes as pt } from 'prop-types';
import { AM, PM } from './TimePicker';
import { MeridiemButtonElement, MeridiemSelectorElement } from './style';

export default class MeridiemSelector extends Component {
    static propTypes = {
        amLabel: pt.node,
        pmLabel: pt.node,
    };

    render() {
        const { amLabel, pmLabel, ...other } = this.props;

        return (
            <MeridiemSelectorElement {...other}>
                <MeridiemButtonElement label={amLabel} value={AM} />
                <MeridiemButtonElement label={pmLabel} value={PM} />
            </MeridiemSelectorElement>
        );
    }
}
