export function noop() {
    // empty
}

export function debounce(func, wait = 0) {
    let handle;

    const debounced = () => {
        if (handle) {
            clearTimeout(handle);
        }

        handle = setTimeout(func, wait);
    };

    debounced.cancel = () => {
        clearTimeout(handle);
    };

    return debounced;
}
